(function() {
  suite('Conditionals', function() {
    test('basic', function() {
      eq('if 0 then 1', generate(new CS.Conditional(new CS.Int(0), new CS.Block([new CS.Int(1)]))));
      return eq('if 0 then 1', generate(new CS.Conditional(new CS.Int(0), new CS.Int(1))));
    });
    test('basic with else', function() {
      eq('if 0 then 1 else 2', generate(new CS.Conditional(new CS.Int(0), new CS.Block([new CS.Int(1)]), new CS.Block([new CS.Int(2)]))));
      return eq('if 0 then 1 else 2', generate(new CS.Conditional(new CS.Int(0), new CS.Int(1), new CS.Int(2))));
    });
    test('multiline', function() {
      return eq('if 0\n  1\n  2', generate(new CS.Conditional(new CS.Int(0), new CS.Block([new CS.Int(1), new CS.Int(2)]))));
    });
    test('multiline with else', function() {
      return eq('if 0\n  1\n  2\nelse\n  3\n  4', generate(new CS.Conditional(new CS.Int(0), new CS.Block([new CS.Int(1), new CS.Int(2)]), new CS.Block([new CS.Int(3), new CS.Int(4)]))));
    });
    test('multiline with basic else', function() {
      eq('if 0\n  1\n  2\nelse\n  3', generate(new CS.Conditional(new CS.Int(0), new CS.Block([new CS.Int(1), new CS.Int(2)]), new CS.Block([new CS.Int(3)]))));
      return eq('if 0\n  1\n  2\nelse\n  3', generate(new CS.Conditional(new CS.Int(0), new CS.Block([new CS.Int(1), new CS.Int(2)]), new CS.Int(3))));
    });
    return test('basic with multiline else', function() {
      eq('if 0\n  1\nelse\n  2\n  3', generate(new CS.Conditional(new CS.Int(0), new CS.Block([new CS.Int(1)]), new CS.Block([new CS.Int(2), new CS.Int(3)]))));
      return eq('if 0\n  1\nelse\n  2\n  3', generate(new CS.Conditional(new CS.Int(0), new CS.Int(1), new CS.Block([new CS.Int(2), new CS.Int(3)]))));
    });
  });

}).call(this);
