(function() {
  suite('Numbers', function() {
    test('simple integers', function() {
      eq('0', generate(new CS.Int(0)));
      return eq('10', generate(new CS.Int(10)));
    });
    test('extremely large numbers should be represented in hex', function() {
      return eq('0xe8d4a51000', generate(new CS.Int(1e12)));
    });
    return test('powers of two should be represented in hex', function() {
      return eq('0x4000', generate(new CS.Int(0x4000)));
    });
  });

}).call(this);
